void read_into(std::string* from, std::vector<int>* into) {
  into->reserve(from->size()); // save on reallocations

  std::transform(std::begin(*from), std::end(*from), std::back_inserter(*into),
      [](char c) {
        return c - '0';
      });
}

int solve(std::vector<int>* num) {
  int prev = num->back();
  int sum = 0;

  for(auto n = num->begin(); n != num->end(); n += 1) {
    std::cout << *n << std::endl;

    if (*n == prev) {
      sum += *n;
    }

    prev = *n;
  }

  std::cout << std::endl;
  return sum;
}

int solve2(std::vector<int>* num) {
  const int half = num->size() / 2;
  int sum = 0;

  for(auto n = num->begin(); n != num->end(); n += 1) {
    std::cout << *n << std::endl;

    if (*n == num->at((half + std::distance(num->begin(), n)) % num->size())) {
      sum += *n;
    }
  }

  std::cout << std::endl;
  return sum;
}

int main(int argc, char* argv[]) {
  std::string* test = &test_input2;
  std::cout << *test << std::endl;

  std::vector<int> numbers;

  read_into(test, &numbers);

  std::cout << solve2(&numbers) << std::endl;
}
